def Hamming_distance (sequencia_1,sequencia_2):
    tamanho = len (sequencia_1)
    distancia = 0
    while tamanho > 0:
        if sequencia_1[tamanho-1] != sequencia_2[tamanho-1]:
            distancia += 1
        tamanho -= 1
    return distancia

seq1 = ""
seq2 = ""

print (Hamming_distance(seq1,seq2))